package com.java.looping;

public class OddOrEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i=2;i<=100;i++)
		{
			if(i%2==0)
			{
				System.out.println(i+" It is a even number");
			}
			else
			{
				System.out.println(i+ "It is a odd number");
			}
		}

	}

}
